    <script>
        $.ajaxSetup({
            headers: {
                'X-CSRF-TOKEN': 'jF4aREBbgXk33J3kbX6hfrvZAualnwjTGHEB0fZA'
            }
        });

        function loader(id) {
            $(id).html('<center><img width="100px" height="100px" src="https://2022.ctl.jatengprov.go.id/media/image/loaders.gif" /> <br /><br /> <h2>memuat data..</h2></center>');
        }

        function removeLoader(id) {
            $(id).html("");
        }

        const select2 = $('.myselect').select2({
            theme: 'bootstrap4'
        });
        let opdVal = () => $('#opd').val();
        let bidangVal = () => $('#bidang').val();
        let programVal = () => $('#program').val();
        let kegiatanVal = () => $('#kegiatan').val();
        let subKegaiatanVal = () => $('#subKegiatan').val();


        function getBidang(val) {
            $.ajax({
                type: "POST",
                url: "https://2022.ctl.jatengprov.go.id/kinerja/opd/sub-kegiatan/bidang",
                data: {
                    opdVal: val
                },
                dataType: "json",
                beforeSend: function() {
                    $("#data-sub-kegiatan").html("");
                },
                success: function(data) {
                    let options = document.getElementById('bidang');
                    $('option', options).remove();
                    for (let i = 0; i < data.length; i++) {
                        options[options.length] = new Option(data[i].uraian, data[i].org_id);
                    }
                }
            }).then(function() {
                getProgram(bidangVal());
            })
        }

        function getProgram(val) {
            $.ajax({
                type: "POST",
                url: "https://2022.ctl.jatengprov.go.id/kinerja/opd/sub-kegiatan/program",
                data: {
                    bidangVal: val
                },
                dataType: "json",
                beforeSend: function() {
                    $("#data-kegiatan").html("");
                },
                error: function() {
                    $('#program').append($('<option>').val("").text(" ~~~ Data Tidak Ditemukan ~~~"));
                },
                success: function(data) {
                    let options = document.getElementById('program');
                    $('option', options).remove();
                    for (let i = 0; i < data.length; i++) {
                        options[options.length] = new Option(data[i].uraian_program, data[i].kr_program_id);
                    }
                }
            }).then(function() {
                getKegiatan(programVal());
            })
        }

        function getKegiatan(val) {
            $.ajax({
                type: "POST",
                url: "https://2022.ctl.jatengprov.go.id/kinerja/opd/sub-kegiatan/kegiatan",
                data: {
                    programVal: val,
                    bidangVal: bidangVal()
                },
                dataType: "json",
                beforeSend: function() {
                    $("#data-sub-kegiatan").html("");
                    loader("#data-sub-kegiatan");
                },
                error: function() {
                    $('#kegiatan').append($('<option>').val("").text(" ~~~ Data Tidak Ditemukan ~~~"));
                    let options = document.getElementById('subKegiatan');
                    $('option', options).remove();
                    $('#subKegiatan').append($('<option>').val("").text(" ~~~ Data Tidak Ditemukan ~~~"));
                },
                success: function(data) {
                    let options = document.getElementById('kegiatan');
                    $('option', options).remove();
                    for (let i = 0; i < data.length; i++) {
                        options[options.length] = new Option(data[i].uraian_kegiatan, data[i].org_sub_keg_id);
                    }
                }
            }).then(function() {
                getSubKegiatan(kegiatanVal());
            })
        }

        function getSubKegiatan(val) {
            $.ajax({
                type: "POST",
                url: "https://2022.ctl.jatengprov.go.id/belanja/sub-sub-kegiatan/get-sub-kegiatan",
                data: {
                    kegiatanVal: val
                },
                dataType: "json",
                beforeSend: function() {
                    $("#data-sub-kegiatan").html("");
                    loader("#data-sub-kegiatan");
                },
                error: function() {
                    $('#subKegiatan').append($('<option>').val("").text(" ~~~ Data Tidak Ditemukan ~~~"));
                },
                success: function(data) {
                    let options = document.getElementById('subKegiatan');
                    $('option', options).remove();
                    for (let i = 0; i < data.length; i++) {
                        $('#subKegiatan').append($('<option>').val(data[i].sub_kegiatan_opd_org_keg_id).text(data[i].uraian));
                    }
                }
            }).then(function() {
                getIndikatorSubKegiatan(subKegaiatanVal());
            })
        }

        function getIndikatorSubKegiatan(val) {
            $.ajax({
                type: "POST",
                url: "https://2022.ctl.jatengprov.go.id/kinerja/opd/sub-kegiatan/indikator",
                data: {
                    org_sub_keg_id: val
                },
                dataType: "html",
                beforeSend: function() {
                    $("#data-sub-kegiatan").html("");
                    loader("#data-sub-kegiatan");
                },
                error: function() {

                },
                success: function(data) {
                    $("#data-sub-kegiatan").html(data);
                }
            }).then(function() {

            })
        }

        $(document).ready(function() {
            getBidang(opdVal());

            $("#subKegiatan").change(function() {
                getIndikatorSubKegiatan(subKegaiatanVal());
            });

            $("#kegiatan").change(function() {
                getSubKegiatan(kegiatanVal());
            });

            $("#program").change(function() {
                getKegiatan(programVal());
            });

        });
    </script>
